import React from 'react';
import { WiHumidity } from "react-icons/wi";

function WeatherTile({ weatherData }) {

    const kelvinToCelsius = (kelvin) => {
        return Math.round((kelvin) - 273.15);
    };

    return (
        <div>
            <div class="flex justify-center items-center flex-col p-6">
                <div class="rounded-lg shadow-lg bg-slate-400 max-w-xl flex flex-col items-center p-6">
                    <h1 className="text-center tracking-wider text-4xl font-mono cursor-pointer text-stone-200 animate-pulse ease-in-out">{weatherData?.name}</h1>
                    <img class="rounded-t-md justify-center animate-bounce ease-out transition-3000 pt-2 mb-0" src={`${process.env.REACT_APP_WEATHER_APP_ICON_URL}/${weatherData?.weather[0]?.icon}@2x.png`} alt="" />
                    <div class="px-6 flex flex-col justify-center items-center text-sm font-mono">
                        <h2 className='text-2xl mb-1 text-stone-800 font-semibold'>{weatherData?.weather[0]?.main} -  {weatherData?.weather[0]?.description}</h2>
                        <h1 className="text-xl">{new Date().toDateString()}</h1>
                        <h2>Temp : <span className='text-lg font-semibold'>{kelvinToCelsius(weatherData?.main?.temp)} deg</span></h2>
                        <h2>Feels like : <span className='text-lg font-semibold'>{kelvinToCelsius(weatherData?.main?.feels_like)} deg</span></h2>
                        <h2>Wind Speed : <span className='text-lg font-semibold'>{weatherData?.wind?.speed} mts/sec</span></h2>
                        <h2>Humidity <span className='text-lg font-semibold'><WiHumidity />: {weatherData?.main?.humidity}%</span></h2>

                        <div className='flex flex-row justify-between items-center'>
                        Humidity <span className='text-lg font-semibold'><WiHumidity />: {weatherData?.main?.humidity}%</span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default WeatherTile
