import React from 'react';
import { WiHumidity, WiCelsius, WiThermometerExterior, WiStrongWind, WiThermometer } from "react-icons/wi";

function WeatherTile({ weatherData }) {

    const kelvinToCelsius = (kelvin) => {
        return Math.round((kelvin) - 273.15);
    };

    const getFormattedCurrentDate = () => {
        return new Date().toLocaleDateString('en-GB', {
            month: '2-digit', day: '2-digit', year: 'numeric'
        });
    }

    return (
        <div>
            <div class="flex justify-center items-center flex-col p-6">
                <div className='flex flex-row justify-between items-center p-2'>
                    <div className='flex flex-col items-center justify-between p-3'>
                    </div>
                    <div class="rounded-xl shadow-xl bg-slate-500 max-w-xl flex flex-col items-center p-3 text-stone-20
                    cursor-pointer hover:bg-gray-100 hover:scale-105 transition transform duration-200 ease-in-out">
                        <h1 className="text-center tracking-wider text-4xl font-mono cursor-pointer text-stone-200">
                            {weatherData?.name}
                        </h1>
                        <h3 className="text-md font-mono text-slate-100">
                            {getFormattedCurrentDate()}
                        </h3>
                        <img class="rounded-t-md justify-center animate-bounce ease-out transition-3000 pt-2 mb-0"
                            src={`${process.env.REACT_APP_WEATHER_APP_ICON_URL}/${weatherData?.weather[0]?.icon}@2x.png`} alt="" />
                        <div class="px-6 flex flex-col justify-center items-center text-sm 0 font-mono text-stone-200">
                            <h2 className='text-2xl mb-1 text-stone-200 font-semibold animate-pulse ease-in-out'>
                                {weatherData?.weather[0]?.main} -  {weatherData?.weather[0]?.description}
                            </h2>
                            <div className='flex flex-row justify-between items-center'>
                                Temp  <WiThermometer size={20} /> :
                                <span className='text-lg font-semibold'>&nbsp;{kelvinToCelsius(weatherData?.main?.temp)} </span>
                                <WiCelsius size={25} />
                            </div>
                            <div className='flex flex-row justify-between items-center'>
                                Feels like  <WiThermometerExterior size={20} /> :
                                <span className='text-lg font-semibold'>&nbsp;{kelvinToCelsius(weatherData?.main?.feels_like)}</span>
                                <WiCelsius size={25} />
                            </div>
                            <hr className="border-2 border-red-500" />
                            <div className='flex flex-row justify-between items-center'>
                                Wind Speed  <WiStrongWind size={20} /> :
                                <span className='text-lg font-semibold'>&nbsp;{weatherData?.wind?.speed} mts/sec</span>
                                <div className='border-solid'></div>
                            </div>
                            <div className='flex flex-row justify-between items-center'>
                                Humidity <WiHumidity size={20} /> :
                                <span className='text-lg font-semibold'>&nbsp;{weatherData?.main?.humidity}%</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div >
    )
}

export default WeatherTile;
