import React, { useEffect, useState } from 'react';
import WeatherTile from './components/WeatherTile';

function WeatherApp() {
    const [latitude, setLatitude] = useState();
    const [longitude, setLongitude] = useState();
    const [data, setData] = useState();

    // called on very first render and everytime the state (lat,long) changes.
    useEffect(() => {
        console.log("Use Effect is Calledd!!");
        const fetchWeatherData = async () => {
            navigator.geolocation.getCurrentPosition((position) => {
                setLatitude(position.coords.latitude);
                setLongitude(position.coords.longitude);
            });
            await fetch(`${process.env.REACT_APP_WEATHER_APP_API_URL}?lat=${latitude}&lon=${longitude}&appid=${process.env.REACT_APP_WEATHER_APP_API_KEY}`)
                .then(res => res.json())
                .then(res => {
                    console.log(res);
                    if (res) {
                        setData(res);
                    } else {
                        setData([]);
                    }
                }).catch(e => console.log(e));
        };
        fetchWeatherData();
    }, [latitude, longitude]);

    return (
        <div>
            <div>

            </div>
            <div>
                {
                    data ?
                        <WeatherTile weatherData={data} /> :
                        <h1>No data found!</h1>
                }
            </div>
        </div>
    )
}

export default WeatherApp
