/* eslint-disable no-undef */
import React, { useEffect, useState } from 'react';
import WeatherTile from './components/WeatherTile';
import Search from './components/Search';

function WeatherApp() {

    const [latitude, setLatitude] = useState();
    const [longitude, setLongitude] = useState();
    const [data, setData] = useState();
    let message;

    // called on very first render and everytime the state (lat,long) changes.
    useEffect(() => {
        console.log("Use Effect is Calledd!!");
        fetchWeatherData();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [latitude, longitude]);

    const fetchWeatherData = async () => {
        navigator.geolocation.getCurrentPosition((position) => {
            const { latitude, longitude } = position.coords;
            setLatitude(latitude);
            setLongitude(longitude);
        }, (err) => {
            console.log(err);
            handleError(err);
        }, { enableHighAccuracy: false, maximumAge: 700_000 });

        if (latitude && longitude) {
            await fetch(`${process.env.REACT_APP_WEATHER_APP_API_URL}?lat=${latitude}&lon=${longitude}&appid=${process.env.REACT_APP_WEATHER_APP_API_KEY}`)
                .then(res => res.json())
                .then(res => {
                    console.log(res);
                    if (res) {
                        setData(res);
                    } else {
                        setData([]);
                    }
                }).catch(e => console.log(e));
        } else {
            message = "Please allow location access to view the weather.";
        }
    };

    // Fetch data based on user entered location
    const fecthData = async (userInput) => {
        const params = new URLSearchParams({
            q: userInput.toString().toLowerCase(),
            limit: 1
        });
        if (userInput) {
            await fetch(`${process.env.REACT_APP_WEATHER_APP_API_URL_GEO}${params.toString()}`)
                .then(res => res.json())
                .then(res => {
                    console.log(res);
                    if (res) {
                        // setData(res);
                    } else {
                        // setData([]);
                    }
                })
                .catch(e => {
                    console.error(e, "API Request to fetch data based on location failed!");
                });
        }
    };

    function handleError(error) {
        // Display error based on the error code.
        const { code } = error;
        switch (code) {
            case GeolocationPositionError.TIMEOUT:
                // Handle timeout.
                message = "Connection timeout."
                break;
            case GeolocationPositionError.PERMISSION_DENIED:
                // User denied the request.
                message = "Please allow location access to view the weather."
                break;
            case GeolocationPositionError.POSITION_UNAVAILABLE:
                // Position not available.
                message = "Weather for current position is not available"
                break;
            default:
                break;
        }
    }

    return (
        <div className="flex flex-col justify-between items-center">
            <Search onSubmit={fecthData} />
            <div className='m-2'>
                {
                    data ?
                        <WeatherTile weatherData={data} /> :
                        <h1>{message}</h1>
                }
            </div>
        </div>
    )
}

export default WeatherApp
